CREATE DATABASE IF NOT EXISTS digital_signage KEEP 365 DURATION 10 BUFFER 256;
USE digital_signage;
CREATE STABLE IF NOT EXISTS gaze_events (ts TIMESTAMP, viewer_id BINARY(20), gaze_time DOUBLE, session_duration DOUBLE, interested INT, attention_rate DOUBLE, age INT, gender BINARY(10), emotion BINARY(20), ad_name BINARY(50)) TAGS (event_type BINARY(20));
